import{_ as o,a as e,b as i,c as r}from"./chunks/preview-4.Bx82POZl.js";import{_ as s,o as n,c as l,ag as a}from"./chunks/framework.xaA4a_mV.js";const w=JSON.parse('{"title":"Introduction","description":"Detailed introduction to TelegramPress (TGNL Admin) project","frontmatter":{"title":"Introduction","description":"Detailed introduction to TelegramPress (TGNL Admin) project","layout":"doc","sidebar":true,"aside":true,"outline":[2,3],"editLink":true,"lastUpdated":true,"head":[["meta",{"name":"keywords","content":"telegram, bot, tron, energy, leasing, admin"}]]},"headers":[],"relativePath":"en/guide/introduction.md","filePath":"en/guide/introduction.md"}'),u={name:"en/guide/introduction.md"};function c(d,t,g,q,h,p){return n(),l("div",null,[...t[0]||(t[0]=[a('<h1 id="introduction" tabindex="-1">Introduction <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;Introduction&quot;">​</a></h1><p><strong>TelegramPress (TGNL Admin)</strong> is a Tron (TRX) energy leasing and automatic delivery system based on Telegram Bot and Web Admin Panel. It integrates energy pool management, automatic monitoring, and user self-service purchasing, aiming to provide convenient energy acquisition services for Tron ecosystem users.</p><h2 id="preview" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect><circle cx="8.5" cy="8.5" r="1.5"></circle><polyline points="21 15 16 10 5 21"></polyline></svg> Preview <a class="header-anchor" href="#preview" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;rect x=&quot;3&quot; y=&quot;3&quot; width=&quot;18&quot; height=&quot;18&quot; rx=&quot;2&quot; ry=&quot;2&quot;&gt;&lt;/rect&gt;&lt;circle cx=&quot;8.5&quot; cy=&quot;8.5&quot; r=&quot;1.5&quot;&gt;&lt;/circle&gt;&lt;polyline points=&quot;21 15 16 10 5 21&quot;&gt;&lt;/polyline&gt;&lt;/svg&gt; Preview&quot;">​</a></h2><div class="screenshot-grid"><div class="screenshot-card"><img src="'+o+'" alt="Dashboard" class="screenshot-img"><div class="screenshot-caption">Dashboard - Real-time Business Monitoring</div></div><div class="screenshot-card"><img src="'+e+'" alt="Bot Management" class="screenshot-img"><div class="screenshot-caption">Bot Management - Visual Configuration</div></div><div class="screenshot-card"><img src="'+i+'" alt="Custom Keyboard" class="screenshot-img"><div class="screenshot-caption">Custom Keyboard - Flexible Interaction</div></div><div class="screenshot-card"><img src="'+r+'" alt="System Settings" class="screenshot-img"><div class="screenshot-caption">System Settings - Global Parameters</div></div></div><h2 id="core-features" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><polygon points="12 2 2 7 12 12 22 7 12 2"></polygon><polyline points="2 17 12 22 22 17"></polyline><polyline points="2 12 12 17 22 12"></polyline></svg> Core Features <a class="header-anchor" href="#core-features" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;polygon points=&quot;12 2 2 7 12 12 22 7 12 2&quot;&gt;&lt;/polygon&gt;&lt;polyline points=&quot;2 17 12 22 22 17&quot;&gt;&lt;/polyline&gt;&lt;polyline points=&quot;2 12 12 17 22 12&quot;&gt;&lt;/polyline&gt;&lt;/svg&gt; Core Features&quot;">​</a></h2><h3 id="smart-bot" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path></svg> Smart Bot <a class="header-anchor" href="#smart-bot" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;20&quot; height=&quot;20&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z&quot;&gt;&lt;/path&gt;&lt;/svg&gt; Smart Bot&quot;">​</a></h3><ul><li><strong>Automated Trading</strong>: 24/7 automatic response to user orders, instant energy delivery.</li><li><strong>Custom Replies</strong>: Support custom keyword replies, welcome messages, and operation guides.</li><li><strong>Multi-level Distribution</strong>: Built-in invitation rebate mechanism to promote user growth.</li></ul><h3 id="powerful-admin-panel" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect><line x1="3" y1="9" x2="21" y2="9"></line><line x1="9" y1="21" x2="9" y2="9"></line></svg> Powerful Admin Panel <a class="header-anchor" href="#powerful-admin-panel" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;20&quot; height=&quot;20&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;rect x=&quot;3&quot; y=&quot;3&quot; width=&quot;18&quot; height=&quot;18&quot; rx=&quot;2&quot; ry=&quot;2&quot;&gt;&lt;/rect&gt;&lt;line x1=&quot;3&quot; y1=&quot;9&quot; x2=&quot;21&quot; y2=&quot;9&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;9&quot; y1=&quot;21&quot; x2=&quot;9&quot; y2=&quot;9&quot;&gt;&lt;/line&gt;&lt;/svg&gt; Powerful Admin Panel&quot;">​</a></h3><ul><li><strong>Visual Dashboard</strong>: Real-time display of key metrics like order volume, turnover, and new users.</li><li><strong>Multi-Pool Scheduling</strong>: Support adding multiple energy pool APIs, smart load balancing to ensure stable supply.</li><li><strong>User Profiling</strong>: Detailed user behavior records, supporting bans, balance adjustments, etc.</li></ul><h3 id="security-stability" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path></svg> Security &amp; Stability <a class="header-anchor" href="#security-stability" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;20&quot; height=&quot;20&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z&quot;&gt;&lt;/path&gt;&lt;/svg&gt; Security &amp; Stability&quot;">​</a></h3><ul><li><strong>Local Private Key Storage</strong>: Payout private keys are encrypted and stored only on the local server to ensure fund security.</li><li><strong>Docker Containerization</strong>: Service isolation, simple deployment, and strong scalability.</li><li><strong>Auto Alerts</strong>: Automatically notify administrators of system anomalies or insufficient energy.</li></ul><h2 id="architecture-overview" tabindex="-1">Architecture Overview <a class="header-anchor" href="#architecture-overview" aria-label="Permalink to &quot;Architecture Overview&quot;">​</a></h2><p>The project consists of the following main components:</p><ul><li><strong>Bot Service (Python)</strong>: Handles Telegram messages, user interactions, order processing, and on-chain interactions.</li><li><strong>Web Service (Nuxt/Node.js)</strong>: Provides Admin API and frontend pages.</li><li><strong>Database (MySQL/Redis)</strong>: Stores user data, order records, and cache queues.</li><li><strong>Nginx</strong>: Acts as a reverse proxy server handling HTTP requests.</li></ul><h2 id="use-cases" tabindex="-1">Use Cases <a class="header-anchor" href="#use-cases" aria-label="Permalink to &quot;Use Cases&quot;">​</a></h2><ul><li><strong>Energy Leasing Providers</strong>: Build your own energy leasing platform and sell energy automatically via TG Bot.</li><li><strong>Tron Developers</strong>: Integrate energy leasing functionality into your own applications.</li><li><strong>Community Managers</strong>: Provide low-cost energy leasing benefits to community members.</li></ul><div class="tip custom-block"><p class="custom-block-title">Note</p><p>The open-source version of this project is for learning and research purposes only. Please comply with relevant open-source licenses for commercial use.</p></div>',17)])])}const y=s(u,[["render",c]]);export{w as __pageData,y as default};
