import{_ as t,a as e,b as i,c as a}from"./chunks/preview-4.Bx82POZl.js";import{_ as s,o as r,c as n,ag as l}from"./chunks/framework.xaA4a_mV.js";const w=JSON.parse('{"title":"Introdução","description":"Introdução detalhada ao projeto TelegramPress (TGNL Admin)","frontmatter":{"title":"Introdução","description":"Introdução detalhada ao projeto TelegramPress (TGNL Admin)","layout":"doc","sidebar":true,"aside":true,"outline":[2,3],"editLink":true,"lastUpdated":true,"head":[["meta",{"name":"keywords","content":"telegram, bot, tron, energy, leasing, admin"}]]},"headers":[],"relativePath":"pt/guide/introduction.md","filePath":"pt/guide/introduction.md"}'),u={name:"pt/guide/introduction.md"};function d(c,o,g,q,p,h){return r(),n("div",null,[...o[0]||(o[0]=[l('<h1 id="introducao" tabindex="-1">Introdução <a class="header-anchor" href="#introducao" aria-label="Permalink to &quot;Introdução&quot;">​</a></h1><p><strong>TelegramPress (TGNL Admin)</strong> é um sistema de aluguel de energia Tron (TRX) e entrega automática baseado no Telegram Bot e Painel de Administração Web. Ele integra gerenciamento de pool de energia, monitoramento automático e compra de autoatendimento do usuário, visando fornecer serviços convenientes de aquisição de energia para usuários do ecossistema Tron.</p><h2 id="visualizacao" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect><circle cx="8.5" cy="8.5" r="1.5"></circle><polyline points="21 15 16 10 5 21"></polyline></svg> Visualização <a class="header-anchor" href="#visualizacao" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;rect x=&quot;3&quot; y=&quot;3&quot; width=&quot;18&quot; height=&quot;18&quot; rx=&quot;2&quot; ry=&quot;2&quot;&gt;&lt;/rect&gt;&lt;circle cx=&quot;8.5&quot; cy=&quot;8.5&quot; r=&quot;1.5&quot;&gt;&lt;/circle&gt;&lt;polyline points=&quot;21 15 16 10 5 21&quot;&gt;&lt;/polyline&gt;&lt;/svg&gt; Visualização&quot;">​</a></h2><div class="screenshot-grid"><div class="screenshot-card"><img src="'+t+'" alt="Painel" class="screenshot-img"><div class="screenshot-caption">Painel - Monitoramento de Negócios em Tempo Real</div></div><div class="screenshot-card"><img src="'+e+'" alt="Gerenciamento de Bot" class="screenshot-img"><div class="screenshot-caption">Gerenciamento de Bot - Configuração Visual</div></div><div class="screenshot-card"><img src="'+i+'" alt="Teclado Personalizado" class="screenshot-img"><div class="screenshot-caption">Teclado Personalizado - Interação Flexível</div></div><div class="screenshot-card"><img src="'+a+'" alt="Configurações do Sistema" class="screenshot-img"><div class="screenshot-caption">Configurações do Sistema - Parâmetros Globais</div></div></div><h2 id="recursos-principais" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><polygon points="12 2 2 7 12 12 22 7 12 2"></polygon><polyline points="2 17 12 22 22 17"></polyline><polyline points="2 12 12 17 22 12"></polyline></svg> Recursos Principais <a class="header-anchor" href="#recursos-principais" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;polygon points=&quot;12 2 2 7 12 12 22 7 12 2&quot;&gt;&lt;/polygon&gt;&lt;polyline points=&quot;2 17 12 22 22 17&quot;&gt;&lt;/polyline&gt;&lt;polyline points=&quot;2 12 12 17 22 12&quot;&gt;&lt;/polyline&gt;&lt;/svg&gt; Recursos Principais&quot;">​</a></h2><h3 id="bot-inteligente" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path></svg> Bot Inteligente <a class="header-anchor" href="#bot-inteligente" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;20&quot; height=&quot;20&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z&quot;&gt;&lt;/path&gt;&lt;/svg&gt; Bot Inteligente&quot;">​</a></h3><ul><li><strong>Negociação Automatizada</strong>: Resposta automática 24/7 aos pedidos dos usuários, entrega instantânea de energia.</li><li><strong>Respostas Personalizadas</strong>: Suporte a respostas por palavras-chave personalizadas, mensagens de boas-vindas e guias de operação.</li><li><strong>Distribuição Multinível</strong>: Mecanismo de reembolso de convite integrado para promover o crescimento de usuários.</li></ul><h3 id="painel-de-administracao-poderoso" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect><line x1="3" y1="9" x2="21" y2="9"></line><line x1="9" y1="21" x2="9" y2="9"></line></svg> Painel de Administração Poderoso <a class="header-anchor" href="#painel-de-administracao-poderoso" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;20&quot; height=&quot;20&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;rect x=&quot;3&quot; y=&quot;3&quot; width=&quot;18&quot; height=&quot;18&quot; rx=&quot;2&quot; ry=&quot;2&quot;&gt;&lt;/rect&gt;&lt;line x1=&quot;3&quot; y1=&quot;9&quot; x2=&quot;21&quot; y2=&quot;9&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;9&quot; y1=&quot;21&quot; x2=&quot;9&quot; y2=&quot;9&quot;&gt;&lt;/line&gt;&lt;/svg&gt; Painel de Administração Poderoso&quot;">​</a></h3><ul><li><strong>Painel Visual</strong>: Exibição em tempo real de métricas importantes como volume de pedidos, faturamento e novos usuários.</li><li><strong>Agendamento Multi-Pool</strong>: Suporte para adicionar várias APIs de pool de energia, balanceamento de carga inteligente para garantir fornecimento estável.</li><li><strong>Perfil de Usuário</strong>: Registros detalhados de comportamento do usuário, suportando banimentos, ajustes de saldo, etc.</li></ul><h3 id="seguranca-e-estabilidade" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path></svg> Segurança e Estabilidade <a class="header-anchor" href="#seguranca-e-estabilidade" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;20&quot; height=&quot;20&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z&quot;&gt;&lt;/path&gt;&lt;/svg&gt; Segurança e Estabilidade&quot;">​</a></h3><ul><li><strong>Armazenamento Local de Chave Privada</strong>: As chaves privadas de pagamento são criptografadas e armazenadas apenas no servidor local para garantir a segurança dos fundos.</li><li><strong>Containerização Docker</strong>: Isolamento de serviços, implantação simples e forte escalabilidade.</li><li><strong>Alertas Automáticos</strong>: Notificar automaticamente os administradores sobre anomalias do sistema ou energia insuficiente.</li></ul><h2 id="visao-geral-da-arquitetura" tabindex="-1">Visão Geral da Arquitetura <a class="header-anchor" href="#visao-geral-da-arquitetura" aria-label="Permalink to &quot;Visão Geral da Arquitetura&quot;">​</a></h2><p>O projeto consiste nos seguintes componentes principais:</p><ul><li><strong>Serviço de Bot (Python)</strong>: Lida com mensagens do Telegram, interações do usuário, processamento de pedidos e interações na cadeia.</li><li><strong>Serviço Web (Nuxt/Node.js)</strong>: Fornece API de administração e páginas de frontend.</li><li><strong>Banco de Dados (MySQL/Redis)</strong>: Armazena dados do usuário, registros de pedidos e filas de cache.</li><li><strong>Nginx</strong>: Atua como um servidor proxy reverso lidando com solicitações HTTP.</li></ul><h2 id="casos-de-uso" tabindex="-1">Casos de Uso <a class="header-anchor" href="#casos-de-uso" aria-label="Permalink to &quot;Casos de Uso&quot;">​</a></h2><ul><li><strong>Provedores de Aluguel de Energia</strong>: Construa sua própria plataforma de aluguel de energia e venda energia automaticamente via TG Bot.</li><li><strong>Desenvolvedores Tron</strong>: Integre a funcionalidade de aluguel de energia em seus próprios aplicativos.</li><li><strong>Gerentes de Comunidade</strong>: Forneça benefícios de aluguel de energia de baixo custo aos membros da comunidade.</li></ul><div class="tip custom-block"><p class="custom-block-title">Nota</p><p>A versão de código aberto deste projeto é apenas para fins de aprendizado e pesquisa. Cumpra as licenças de código aberto relevantes para uso comercial.</p></div>',17)])])}const k=s(u,[["render",d]]);export{w as __pageData,k as default};
