import{_ as o,o as e,c as i,ag as l}from"./chunks/framework.xaA4a_mV.js";const g=JSON.parse('{"title":"部署指南","description":"Telegram 五合一机器人部署教程","frontmatter":{"title":"部署指南","description":"Telegram 五合一机器人部署教程","layout":"doc","sidebar":true,"aside":true,"outline":[2,3],"lastUpdated":true},"headers":[],"relativePath":"tgbot-Ultra/getting-started.md","filePath":"tgbot-Ultra/getting-started.md"}'),a={name:"tgbot-Ultra/getting-started.md"};function u(n,t,s,q,r,h){return e(),i("div",null,[...t[0]||(t[0]=[l(`<h1 id="部署指南" tabindex="-1">部署指南 <a class="header-anchor" href="#部署指南" aria-label="Permalink to &quot;部署指南&quot;">​</a></h1><p>本指南将帮助您从零开始部署 Telegram 五合一全能机器人。</p><h2 id="环境准备" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><rect x="4" y="4" width="16" height="16" rx="2" ry="2"></rect><rect x="9" y="9" width="6" height="6"></rect><line x1="9" y1="1" x2="9" y2="4"></line><line x1="15" y1="1" x2="15" y2="4"></line><line x1="9" y1="20" x2="9" y2="23"></line><line x1="15" y1="20" x2="15" y2="23"></line><line x1="20" y1="9" x2="23" y2="9"></line><line x1="20" y1="14" x2="23" y2="14"></line><line x1="1" y1="9" x2="4" y2="9"></line><line x1="1" y1="14" x2="4" y2="14"></line></svg> 环境准备 <a class="header-anchor" href="#环境准备" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;rect x=&quot;4&quot; y=&quot;4&quot; width=&quot;16&quot; height=&quot;16&quot; rx=&quot;2&quot; ry=&quot;2&quot;&gt;&lt;/rect&gt;&lt;rect x=&quot;9&quot; y=&quot;9&quot; width=&quot;6&quot; height=&quot;6&quot;&gt;&lt;/rect&gt;&lt;line x1=&quot;9&quot; y1=&quot;1&quot; x2=&quot;9&quot; y2=&quot;4&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;15&quot; y1=&quot;1&quot; x2=&quot;15&quot; y2=&quot;4&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;9&quot; y1=&quot;20&quot; x2=&quot;9&quot; y2=&quot;23&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;15&quot; y1=&quot;20&quot; x2=&quot;15&quot; y2=&quot;23&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;20&quot; y1=&quot;9&quot; x2=&quot;23&quot; y2=&quot;9&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;20&quot; y1=&quot;14&quot; x2=&quot;23&quot; y2=&quot;14&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;1&quot; y1=&quot;9&quot; x2=&quot;4&quot; y2=&quot;9&quot;&gt;&lt;/line&gt;&lt;line x1=&quot;1&quot; y1=&quot;14&quot; x2=&quot;4&quot; y2=&quot;14&quot;&gt;&lt;/line&gt;&lt;/svg&gt; 环境准备&quot;">​</a></h2><ol><li><strong>服务器</strong>：一台海外服务器（建议配置 2核4G 以上）。</li><li><strong>域名</strong>：一个已解析到服务器 IP 的域名。</li><li><strong>宝塔面板</strong>：建议安装宝塔面板以简化环境配置。</li></ol><h2 id="部署流程" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path><polyline points="7 10 12 15 17 10"></polyline><line x1="12" y1="15" x2="12" y2="3"></line></svg> 部署流程 <a class="header-anchor" href="#部署流程" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4&quot;&gt;&lt;/path&gt;&lt;polyline points=&quot;7 10 12 15 17 10&quot;&gt;&lt;/polyline&gt;&lt;line x1=&quot;12&quot; y1=&quot;15&quot; x2=&quot;12&quot; y2=&quot;3&quot;&gt;&lt;/line&gt;&lt;/svg&gt; 部署流程&quot;">​</a></h2><h3 id="_1-基础环境安装" tabindex="-1">1. 基础环境安装 <a class="header-anchor" href="#_1-基础环境安装" aria-label="Permalink to &quot;1. 基础环境安装&quot;">​</a></h3><p>在宝塔面板中安装以下软件：</p><ul><li>Nginx 1.20+</li><li>MySQL 5.7+</li><li>PHP 7.4+ (需安装 Redis、fileinfo 扩展)</li><li>Redis 6.0+</li></ul><h3 id="_2-创建网站" tabindex="-1">2. 创建网站 <a class="header-anchor" href="#_2-创建网站" aria-label="Permalink to &quot;2. 创建网站&quot;">​</a></h3><p>在宝塔中添加一个新站点，绑定您的域名，并创建一个新的 MySQL 数据库。</p><h3 id="_3-上传源码" tabindex="-1">3. 上传源码 <a class="header-anchor" href="#_3-上传源码" aria-label="Permalink to &quot;3. 上传源码&quot;">​</a></h3><p>将购买的源码包上传至网站根目录，并解压。</p><h3 id="_4-导入数据库" tabindex="-1">4. 导入数据库 <a class="header-anchor" href="#_4-导入数据库" aria-label="Permalink to &quot;4. 导入数据库&quot;">​</a></h3><p>将源码包中的 <code>database.sql</code> 文件导入到刚刚创建的数据库中。</p><h3 id="_5-修改配置" tabindex="-1">5. 修改配置 <a class="header-anchor" href="#_5-修改配置" aria-label="Permalink to &quot;5. 修改配置&quot;">​</a></h3><p>编辑网站根目录下的 <code>config/database.php</code>，填入您的数据库信息。</p><h3 id="_6-配置伪静态" tabindex="-1">6. 配置伪静态 <a class="header-anchor" href="#_6-配置伪静态" aria-label="Permalink to &quot;6. 配置伪静态&quot;">​</a></h3><p>在网站设置中，选择 <code>ThinkPHP</code> 伪静态规则并保存。</p><h3 id="_7-启动机器人" tabindex="-1">7. 启动机器人 <a class="header-anchor" href="#_7-启动机器人" aria-label="Permalink to &quot;7. 启动机器人&quot;">​</a></h3><p>在终端进入网站目录，执行启动命令：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">cd</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /www/wwwroot/您的域名</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">php</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> think</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> swoole</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> start</span></span></code></pre></div><div class="tip custom-block"><p class="custom-block-title">守护进程</p><p>建议使用 Supervisor 或 Systemd 将启动命令配置为守护进程，以保证机器人长期稳定运行。</p></div>`,22)])])}const c=o(a,[["render",u]]);export{g as __pageData,c as default};
