import{_ as o,o as e,c as l,ag as n}from"./chunks/framework.xaA4a_mV.js";const q=JSON.parse('{"title":"项目介绍","description":"Telegram 五合一全能机器人解决方案","frontmatter":{"title":"项目介绍","description":"Telegram 五合一全能机器人解决方案","layout":"doc","sidebar":true,"aside":true,"outline":[2,3],"lastUpdated":true},"headers":[],"relativePath":"tgbot-Ultra/introduction.md","filePath":"tgbot-Ultra/introduction.md"}'),r={name:"tgbot-Ultra/introduction.md"};function a(i,t,s,g,u,d){return e(),l("div",null,[...t[0]||(t[0]=[n('<h1 id="项目介绍" tabindex="-1">项目介绍 <a class="header-anchor" href="#项目介绍" aria-label="Permalink to &quot;项目介绍&quot;">​</a></h1><p><strong>Telegram 五合一机器人</strong> 是一款全开源、无加密的综合型运营机器人，集成了 <strong>TRX 兑换</strong>、<strong>能量租赁</strong>、<strong>会员充值</strong>、<strong>钱包监控</strong> 和 <strong>发卡系统</strong> 五大核心功能。配合独立的 PHP 后台，您可以轻松管理所有业务，是 Telegram 生态变现的终极利器。</p><div class="tip custom-block"><p class="custom-block-title">2024.10.1 最新版</p><p>我们已与原创作者深度合作推出平台专属版，修复了已知问题，并支持独立部署 API 端。</p></div><h2 id="五大核心模块" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><polygon points="12 2 2 7 12 12 22 7 12 2"></polygon><polyline points="2 17 12 22 22 17"></polyline><polyline points="2 12 12 17 22 12"></polyline></svg> 五大核心模块 <a class="header-anchor" href="#五大核心模块" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;polygon points=&quot;12 2 2 7 12 12 22 7 12 2&quot;&gt;&lt;/polygon&gt;&lt;polyline points=&quot;2 17 12 22 22 17&quot;&gt;&lt;/polyline&gt;&lt;polyline points=&quot;2 12 12 17 22 12&quot;&gt;&lt;/polyline&gt;&lt;/svg&gt; 五大核心模块&quot;">​</a></h2><h3 id="_1-trx-兑换系统" tabindex="-1">1. TRX 兑换系统 <a class="header-anchor" href="#_1-trx-兑换系统" aria-label="Permalink to &quot;1. TRX 兑换系统&quot;">​</a></h3><ul><li><strong>自动兑换</strong>：支持 USDT 与 TRX 的双向自动兑换。</li><li><strong>汇率管理</strong>：后台可实时调整汇率差价，实现自动化盈利。</li></ul><h3 id="_2-能量租赁平台" tabindex="-1">2. 能量租赁平台 <a class="header-anchor" href="#_2-能量租赁平台" aria-label="Permalink to &quot;2. 能量租赁平台&quot;">​</a></h3><ul><li><strong>灵活模式</strong>：支持自有质押能量，也支持对接第三方 API 平台。</li><li><strong>轮询机制</strong>：支持多平台 API 轮询，保障能量供应的稳定性。</li></ul><h3 id="_3-telegram-会员充值" tabindex="-1">3. Telegram 会员充值 <a class="header-anchor" href="#_3-telegram-会员充值" aria-label="Permalink to &quot;3. Telegram 会员充值&quot;">​</a></h3><ul><li><strong>官方接口</strong>：采用官方正规接口，安全稳定。</li><li><strong>自定义套餐</strong>：针对不同的会员平台或时长，设置差异化的价格套餐。</li></ul><h3 id="_4-钱包地址监控" tabindex="-1">4. 钱包地址监控 <a class="header-anchor" href="#_4-钱包地址监控" aria-label="Permalink to &quot;4. 钱包地址监控&quot;">​</a></h3><ul><li><strong>实时通知</strong>：监听指定钱包地址的资金变动，并发送实时通知。</li><li><strong>付费服务</strong>：可将监控功能作为付费服务出售给用户。</li></ul><h3 id="_5-综合发卡系统" tabindex="-1">5. 综合发卡系统 <a class="header-anchor" href="#_5-综合发卡系统" aria-label="Permalink to &quot;5. 综合发卡系统&quot;">​</a></h3><ul><li><strong>虚拟商品</strong>：支持卡密、账号等各类虚拟商品的自动售卖与发货。</li><li><strong>库存管理</strong>：完善的库存管理与补货提醒。</li></ul><h2 id="价格方案" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M12 2v20M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path></svg> 价格方案 <a class="header-anchor" href="#价格方案" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M12 2v20M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6&quot;&gt;&lt;/path&gt;&lt;/svg&gt; 价格方案&quot;">​</a></h2><p>我们提供源码交付和部署服务，支持整套购买或单独功能购买。</p><h3 id="单客户端版本" tabindex="-1">单客户端版本 <a class="header-anchor" href="#单客户端版本" aria-label="Permalink to &quot;单客户端版本&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">方案</th><th style="text-align:center;">价格 (USDT)</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>单源码</strong></td><td style="text-align:center;"><strong>500 U</strong></td><td style="text-align:left;">仅源码，保证完整性，不含教学</td></tr><tr><td style="text-align:left;"><strong>单套部署</strong></td><td style="text-align:center;"><strong>1000 U</strong></td><td style="text-align:left;">含部署服务，教会使用</td></tr><tr><td style="text-align:left;"><strong>无限部署教学</strong></td><td style="text-align:center;"><strong>1850 U</strong></td><td style="text-align:left;">手把手教学，学会后可无限接单</td></tr><tr><td style="text-align:left;"><strong>单功能散卖</strong></td><td style="text-align:center;"><strong>225 U</strong></td><td style="text-align:left;">仅购买五大功能中的某一项</td></tr></tbody></table><h3 id="客户端-独立-api-端-推荐" tabindex="-1">客户端 + 独立 API 端 (推荐) <a class="header-anchor" href="#客户端-独立-api-端-推荐" aria-label="Permalink to &quot;客户端 + 独立 API 端 (推荐)&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">方案</th><th style="text-align:center;">价格 (USDT)</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>单源码</strong></td><td style="text-align:center;"><strong>900 U</strong></td><td style="text-align:left;">包含客户端与 API 端全套源码</td></tr><tr><td style="text-align:left;"><strong>单套部署</strong></td><td style="text-align:center;"><strong>1300 U</strong></td><td style="text-align:left;">全套部署，教会使用</td></tr><tr><td style="text-align:left;"><strong>无限部署教学</strong></td><td style="text-align:center;"><strong>1950 U</strong></td><td style="text-align:left;">全套技术教学，适合开发者或代理</td></tr><tr><td style="text-align:left;"><strong>单功能散卖</strong></td><td style="text-align:center;"><strong>275 U</strong></td><td style="text-align:left;">仅购买某一项功能的加强版</td></tr></tbody></table><h2 id="技术特性" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z"></path><polyline points="3.27 6.96 12 12.01 20.73 6.96"></polyline><line x1="12" y1="22.08" x2="12" y2="12"></line></svg> 技术特性 <a class="header-anchor" href="#技术特性" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M21 16V8a2 2 0 0 0-1-1.73l-7-4a2 2 0 0 0-2 0l-7 4A2 2 0 0 0 3 8v8a2 2 0 0 0 1 1.73l7 4a2 2 0 0 0 2 0l7-4A2 2 0 0 0 21 16z&quot;&gt;&lt;/path&gt;&lt;polyline points=&quot;3.27 6.96 12 12.01 20.73 6.96&quot;&gt;&lt;/polyline&gt;&lt;line x1=&quot;12&quot; y1=&quot;22.08&quot; x2=&quot;12&quot; y2=&quot;12&quot;&gt;&lt;/line&gt;&lt;/svg&gt; 技术特性&quot;">​</a></h2><ul><li><strong>全开源</strong>：无任何加密文件，支持二次开发。</li><li><strong>PHP 后台</strong>：成熟稳定的 PHP 架构，易于维护。</li><li><strong>高度自定义</strong>：命令、键盘、关键词回复、定时广告均可自由配置。</li><li><strong>多机器人</strong>：一套系统可管理多个机器人实例。</li></ul>',22)])])}const c=o(r,[["render",a]]);export{q as __pageData,c as default};
