import{_ as o,o as n,c as l,ag as i}from"./chunks/framework.xaA4a_mV.js";const h=JSON.parse('{"title":"项目介绍","description":"TGPro Admin 系统功能介绍","frontmatter":{"title":"项目介绍","description":"TGPro Admin 系统功能介绍","layout":"doc","sidebar":true,"aside":true,"outline":[2,3],"lastUpdated":true},"headers":[],"relativePath":"tgpro-admin/introduction.md","filePath":"tgpro-admin/introduction.md"}'),r={name:"tgpro-admin/introduction.md"};function e(s,t,u,a,g,q){return n(),l("div",null,[...t[0]||(t[0]=[i('<h1 id="项目介绍" tabindex="-1">项目介绍 <a class="header-anchor" href="#项目介绍" aria-label="Permalink to &quot;项目介绍&quot;">​</a></h1><p><strong>TGPro Admin</strong> 是一套功能强大的 Telegram 机器人管理系统，旨在为用户提供一站式的机器人运营解决方案。</p><h2 id="核心功能" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><polygon points="12 2 2 7 12 12 22 7 12 2"></polygon><polyline points="2 17 12 22 22 17"></polyline><polyline points="2 12 12 17 22 12"></polyline></svg> 核心功能 <a class="header-anchor" href="#核心功能" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;polygon points=&quot;12 2 2 7 12 12 22 7 12 2&quot;&gt;&lt;/polygon&gt;&lt;polyline points=&quot;2 17 12 22 22 17&quot;&gt;&lt;/polyline&gt;&lt;polyline points=&quot;2 12 12 17 22 12&quot;&gt;&lt;/polyline&gt;&lt;/svg&gt; 核心功能&quot;">​</a></h2><ul><li><strong>多币种支付支持</strong>：原生支持 USDT (TRC20) 和 TON 支付，满足不同用户的支付习惯。</li><li><strong>自动发货系统</strong>：用户支付成功后，系统自动检测并即时发货，无需人工干预。</li><li><strong>强大的后台管理</strong>：提供可视化的 Web 管理后台，轻松配置机器人参数、查看订单状态、管理用户数据。</li><li><strong>数据统计分析</strong>：直观的仪表盘展示，实时掌握今日订单、总流水、新增用户等关键运营指标。</li><li><strong>消息广播功能</strong>：支持向所有用户或特定用户组发送广播消息，高效触达用户。</li></ul><h2 id="安全与稳定" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path></svg> 安全与稳定 <a class="header-anchor" href="#安全与稳定" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z&quot;&gt;&lt;/path&gt;&lt;/svg&gt; 安全与稳定&quot;">​</a></h2><ul><li><strong>私钥本地存储</strong>：支付私钥仅存储在您部署的本地服务器中，确保资金绝对安全。</li><li><strong>Docker 容器化</strong>：基于 Docker 架构，服务隔离，部署简便，迁移无忧。</li><li><strong>高性能架构</strong>：后端采用 Go 和 Python 混合开发，前端采用 Nuxt.js，兼顾开发效率与运行性能。</li></ul><h2 id="适用场景" tabindex="-1"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="title-icon"><path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20"></path><path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z"></path></svg> 适用场景 <a class="header-anchor" href="#适用场景" aria-label="Permalink to &quot;&lt;svg xmlns=&quot;http://www.w3.org/2000/svg&quot; width=&quot;24&quot; height=&quot;24&quot; viewBox=&quot;0 0 24 24&quot; fill=&quot;none&quot; stroke=&quot;currentColor&quot; stroke-width=&quot;2&quot; stroke-linecap=&quot;round&quot; stroke-linejoin=&quot;round&quot; class=&quot;title-icon&quot;&gt;&lt;path d=&quot;M4 19.5A2.5 2.5 0 0 1 6.5 17H20&quot;&gt;&lt;/path&gt;&lt;path d=&quot;M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z&quot;&gt;&lt;/path&gt;&lt;/svg&gt; 适用场景&quot;">​</a></h2><ul><li><strong>数字商品售卖</strong>：自动售卖激活码、账号、会员等虚拟商品。</li><li><strong>付费群组管理</strong>：支付后自动拉入 VIP 群组或频道。</li><li><strong>内容订阅服务</strong>：提供付费内容订阅和自动推送服务。</li></ul>',8)])])}const p=o(r,[["render",e]]);export{h as __pageData,p as default};
